import React from "react";
import Header from "./Header"
import Footer from "./Footer"
import Create from "./Create"
import Idea from "./Idea"

function App() {

const [ideas, setIdeas] = React.useState([]);

function addIdea(newIdea) {
    setIdeas(prevIdeas => {
        [...prevIdeas, newIdea]
    })
}

    return <div>
        <Header />
        <Create 
            onAdd={addIdea}
        />
        <Idea />
        <Footer />
    </div>
};

export default App;
